import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { LoginService } from './login.service';
import { ToastrService } from 'ngx-toastr';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  loginForm!: FormGroup;
  show:boolean =false
  password: any;

  constructor(private fb: FormBuilder,private LoginService:LoginService,private toastr: ToastrService) {}

  ngOnInit() {
    this.password = 'password';
    this.initLoginForm();
  }

  initLoginForm(){
    this.loginForm = this.fb.group({
      email: ['', Validators.required],
      password: ['', Validators.required],
      check_box:['',Validators.required]
    });
  }
  get f() {
    return this.loginForm.controls;
  }
  onClick() {
    if (this.password === 'password') {
      this.password = 'text';
      this.show = true;
    } else {
      this.password = 'password';
      this.show = false;
    }
  }

  onSubmit() {
    console.log(this.loginForm)
   if(this.loginForm.valid){
    this.LoginService.saveLogin(this.loginForm.getRawValue()).subscribe((result: any) => {
      this.toastr.success('succesfully Login!');
  })
}}

 }
